import COLOR from '../comps/style/style'
import { connect } from 'react-redux'
import React, { Component } from 'react'
import { Form, FormGroup, Input, Col, Button } from 'reactstrap'


import { getUser, createUser } from '../redux/_actions/user'


class Profile extends Component {

    constructor() {
        super()
        this.state = {
            isChangeUser: false,
            user: 'user',
            memo: {
                title: 'add new title here',
                post: 'how was your day ?'
            }
        }
    }

    _addUser = async () => {
        await this.setState({
            isChangeUser: false
        })
        console.log(this.state.user)
        // await this.props.dispatch(createUser(this.state.user))
    }

    getInitialProps() {
        this.props.dispatch(getUser())
        console.log(this.state.user)
    }
    render() {
        return (
            <div style={styles.layout} className='container'>
                <div style={styles.upText}>
                    <div style={styles.head}>
                        <h3><b>Hi, </b></h3>
                        {
                            this.state.isChangeUser == false &&
                            <h3><b>{this.state.user}</b></h3>
                        }
                        {
                            this.state.isChangeUser == true &&
                            <Input type="name" placeholder="What's is your name ?" onChange={(user) => {
                                this.setState({
                                    user
                                })
                            }} />
                        }
                    </div>
                    {
                        this.state.isChangeUser == false &&
                        <Button onClick={() => { this.setState({ isChangeUser: true }) }}>edit</Button>
                    }
                    {
                        this.state.isChangeUser == true &&
                        <Button onClick={() => this._addUser()}>Done</Button>
                    }
                </div>
                <h5 style={{ ...styles.textHead, color: COLOR.lightBlue }}>Did anything suprise you today ?</h5>

                <div style={styles.form}>
                    <Form>
                        <FormGroup row>
                            <Col>
                                <Input type="email" placeholder="add a title here" />
                            </Col>
                        </FormGroup>

                        <FormGroup row>
                            <Col>
                                <Input type="textarea" style={styles.textarea} placeholder="how was your day ?" />
                            </Col>
                        </FormGroup>

                        <Button style={styles.button} className='float-right' >Send</Button>
                    </Form>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        user: state.user
    }
}

export default connect(mapStateToProps)(Profile)

const styles = {
    layout: {
        flex: 1,
        marginTop: 50,
        paddingTop: 20
    },
    textHead: {
        color: COLOR.black
    },
    upText: {
        display: 'flex',
        flexDirection: 'row',
        'justify-content': 'space-between',
    },
    head: {
        display: 'flex',
        flexDirection: 'row',
        'justify-content': 'space-evenly'
    },
    form: {
        marginTop: 50
    },
    textarea: {
        height: 200
    },
    button: {
        width: 100
    }
}
